function loadGroup () {
    let link = window.location.href;
    let linkPlace = document.getElementById('link');
    let backLink = document.getElementById('back');
    let placement = document.getElementById('place');
    let cutLink = link.substring(link.lastIndexOf('#') + 1);

    if (cutLink.length == 0) window.location.href = 'https://d5dh3gkmd44pmenjhm99.apigw.yandexcloud.net/search';

    let upLink = cutLink.toUpperCase();
    let incl = 0;
    let found = '';

    for (let check in groupList) {
        let l = groupList[check].link;
        if (l == upLink) {
            found = check;
            console.log(found);
            incl = 1;
        }
    }

    if (incl == 0) {
        linkPlace.outerHTML = `<a href="detail#empty" class="active_link" id="link">Страница не найдена</a></div>`;
        placement.innerHTML = `<p>Такой страницы нет. Воспользуйтесь <a href="search">поиском</a> или <a href="https://kpop.fandom.com/ru/wiki/K-pop_вики">K-pop Wiki</a></p>`;
    } else {
        let searched = groupList[found];
        let backPage = '';

        switch (searched.category) {
            case 'boy': backPage = 'Мужские группы';
                break;
            case 'girl': backPage = 'Женские группы';
                break;
            case 'mix': backPage = 'Смешанные группы';
                break;
            case 'solo': backPage = 'Сольные исполнители';
                break;
        }

        backLink.outerHTML = `<a href="${searched.category}" class="active_link" id="link">${backPage}</a></div>`;
        linkPlace.outerHTML = `<a href="detail#${upLink}" class="active_link" id="link">${found}</a></div>`;
        if (searched.type == 'group') {
            placement.innerHTML = `<h1>${found}</h1>
            <div class='info-card'>
                <div class='search_info'>
                    <img src='${searched.pic}'><br>
            
                    Дебют: ${searched.debute}<br>
                    Агенство: ${searched.agency}<br>
                    Лидер: ${searched.leader}<br>
                    Фандом: ${searched.fandom}
                </div>
                <div class='general_info'>
                    ${searched.short}
                </div>
            </div>
            <div class='main-text'>
                ${searched.text}
                <h4>Участники:</h4>
                <p>${searched.members}</p>
                <h4>Бывшие участники:</h4>
                <p>${searched.ex}</p>
                <h3>Дискография</h3>
                ${searched.alboms}
            </div>`
        }
        if (searched.type == 'artist') {
            placement.innerHTML = `<h1>${upLink}</h1>
                <div class='info-card'>
					<div class='search_info'>
					<img src='${searched.pic}'><br>
					
						Имя: ${searched.name}<br>
						Псевдонимы: ${searched.alias}<br>
						Дата рождения: ${searched.birth}<br>
						Сольный дебют: ${searched.debute}<br>
						Агенство: ${searched.agency}<br>
						Фандом: ${searched.fandom}
					</div>
					<div class='general_info'>
						${searched.short}
					</div>
				</div>
                <div class='main-text'>
                    ${searched.text}
                    <h3>Дискография</h3>
                    ${searched.alboms}
                </div>`;
        }
    }
}

